from typing import List

class Solution:
    def maxDistance(self, position: List[int], m: int) -> int:
        position.sort()

        total_distance=position[-1]-position[0]
        if m==2:
            return total_distance
        
        max_distance=total_distance//(m-1)
        def bi(start,target):
            l,r=start,len(position)-1
            mid=(l+r)//2
            while l<r:
                if position[mid]>=target and position[mid-1]<target:
                    break
                elif position[mid]>target:
                    r=mid-1
                elif position[mid]<target:
                    l=mid+1
                mid=(l+r)//2
            return mid

        use_map=False
        if max_distance<100000:
            valid_map=[None for _ in range(max_distance+1)]
            use_map=True


        def valid(distance:int)->bool:
            if use_map:
                if type(valid_map[distance])==bool:
                    return valid_map[distance]
            start_point=0
            for i in range(m-1):
                target=position[start_point]+distance
                # print(f"i={i}, target={target}")
                if target>position[-1]:
                    result=False
                    break
                
                start_point=bi(start_point,target)
                # print(f"good point={start_point}")
                result=True
            if use_map:
                valid_map[distance]=result
            return result


        # min_dis=float("inf")
        # for i in range(1,len(position)-1):
        #     if position[i]-position[i-1]<min_dis:
        #         min_dis=position[i]-position[i-1]



        l,r=0,max_distance
        mid=(l+r)//2
        while l<r:
            # print(f"l={l} ,r={r} ,mid={mid},{valid(mid)},{valid(mid+1)}")
            if valid(mid)==True and valid(mid+1)==False:
                print("here")
                return mid
            elif valid(mid):
                l=mid+1
            elif valid(mid)==False:
                r=mid
            mid=(l+r)//2
        # print(f"l={l} ,r={r} ,mid={mid}")
        return mid


